import React, { useState } from 'react';
import './App.css';
import Gamegrid from './components/Game_grid';

function App() {

  const [start, setStart] = useState(false);


  const startGame =() => {
    setStart(true);
  }

  const resetGame = () => {
    document.querySelector('.sortOrder').innerHTML='<option value="1">1</option> <option value="2">2</option> <option value="3">3</option><option value="4">4</option>';
  }
  return (

    <div className="grid-container">
      <header className="header">
        <div> <button type="button" className="button" onClick={resetGame}>Reset</button></div>
        <div>Snake and ladder Game</div>
        <div> <button type="button" className="button" onClick={startGame}>Start</button></div>
      </header>

      <main className="main">
        <Gamegrid startFlag={start}/>
      </main>

      <footer className="footer">
        <div> Please Select Color box</div>
      </footer>
    </div>

  );
}

export default App;
