import React, { useState } from 'react';

function Dice(props) {
    const [dice, setDice] = useState(1);
    const [playerScore, setPlayerScore] = useState(0);
    let select = document.querySelector(`.cell${playerScore}`);

    const rollDice =() => {
        let randomNumber = Math.floor(Math.random() * 3) + 1;
        let score = playerScore + randomNumber;
        setDice(randomNumber);       
        // setPlayerScore(score);
        props.totalScoreCalc(score);
        document.querySelector(`.cell${playerScore}`).innerHTML=`<div> </div>`;
    }

    if(select){
        select.innerHTML= `<div class="circular red red_position"> </div>`;
    }
    
    
    console.log(props.startFlag);
    return (
    <div>
        {props.startFlag 
        ? 
            <img className="dice_image" src={`/images/dice-${dice}.png`} onClick={rollDice}></img>
        : <div>Please Click on Start button to start th Game</div>
        }
        
        {(playerScore>14) && (<div>Player1 is winner</div>)}
    </div>
    
    );
}

export default Dice;
