import React from "react";

function CircleColorSet(props){
    return(
        <div>
            
            {props.numberOfPlayers === 1 
            && <div className="circular red">2</div> }

            {props.numberOfPlayers === 2 
            &&  <>
                    <div className="circular red">2</div> 
                    <div className="circular blue">2</div>
                </>
            }
            {props.numberOfPlayers === 3 
            &&  <>
                    <div className="circular red">2</div> 
                    <div className="circular blue">2</div>
                    <div className="circular yellow">2</div>
                </>
            }
            {props.numberOfPlayers === 4 
            &&  <>
                    <div className="circular red">2</div> 
                    <div className="circular blue">2</div>
                    <div className="circular yellow">2</div>
                    <div className="circular green">2</div>
                </>
            }
        </div>
        
    );
}

export default CircleColorSet;
