import React, { useState } from 'react';

function Dice(props) {
    const [dice, setDice] = useState(1);
    // const [playerScore, setPlayerScore] = useState(0);
    // let select = document.querySelector(`.cell${props.playerScore}Yellow`);

    const rollDice =() => {
        let randomNumber = Math.floor(Math.random() * 3) + 1;
        let score = props.playerScore + randomNumber;
        setDice(randomNumber);       
        // setPlayerScore(score);
        props.totalScoreCalc(score);
        // document.querySelector(`.cell${props.playerScore}Red`).innerHTML=`<div> </div>`;
    }

    // if(select){
    //     select.innerHTML= `<div class="circular yellow"> </div>`;
    //     document.querySelector('.colorBox').innerHTML = '<div class="circular red"></div><div class="circular blue"></div>  <div class="circular green"}></div> <div class="circular yellow"></div>'
    // }
    
    return (
    <div>
        {props.startFlag 
        ? 
            <img className="dice_image" src={`/images/dice-${dice}.png`} alt={`dice-${dice}`} onClick={rollDice}></img>
        : <div>Please Click on Start button to start th Game</div>
        }
        
        {(props.playerScore>14) && (<div>Player1 is winner</div>)}
    </div>
    
    );
}

export default Dice;
