import React, { useState } from "react";
import MainBottom from "./MainBottom";

function Game_grid(){
    const numbers = ['win',14,13,12,11,10,9,8,7,6,5,4,'start',1,2,3];
    const [numberPlayers, setNumberPlayers] = useState('');

    const onNumberChange =(event) => {
        setNumberPlayers(event.target.value);
    }

    console.log(numberPlayers);

    return (
        <div>
            <div className="input_item">         
                Select Number of Players
                <select className="sortOrder" onChange= {onNumberChange}>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                </select>
            </div>
            <div className="ui_grid">
                {numbers.map(number => <div className="cell" key={number}>{number}</div>)}
            </div>
            <div className="main_bttom">
                <MainBotto players_number={numberPlayers}/>
            </div>
        </div>
    );
}


export default Game_grid;
