import React, { useState } from 'react';

function Dice() {
    const [dice, setDice] = useState(1);
    const [playerScore, setPlayerScore] = useState(0);

    const rollDice =() => {
        let randNumber = Math.floor(Math.random() * 3) + 1;
        setDice(randNumber);
        let score = playerScore + randomNumber;
        setPlayerScore(score);
    }
    
    console.log(playerScore);
    return (
    <div>
        {/* <button className="button" onClick={rollDice}>{dice}</button> */}
        <img className="dice_image" src={`/images/dice-${dice}.png`} onClick={rollDice}></img>
        Roll Dice
    </div>
    
    );
}

export default Dice;
