import React, { useState } from 'react';
import './App.css';
import Gamegrid from './components/Game_grid';

function App() {

  const [start, setStart] = useState(false);
  const [numberPlayers, setNumberPlayers] = useState(1);
  const [activePlayer, setActivePlayer]=useState(1);
  const [playerScore , setPlayerScore] = useState(
                                  {
                                    player1: 0,
                                    player2: 0,
                                    player3: 0,
                                    player4: 0
                                  });

  let select_Player1 = document.querySelector(`.cell${playerScore.player1}Red`);
  let select_Player2 = document.querySelector(`.cell${playerScore.player2}Blue`);
  let select_Player3 = document.querySelector(`.cell${playerScore.player3}Green`);
  let select_Player4 = document.querySelector(`.cell${playerScore.player4}Yellow`);
  const arr = [select_Player1,select_Player2,select_Player3,select_Player4];

  if(select_Player1){
    for(let i=0; i< parseInt(numberPlayers); i++){
       arr[i].innerHTML= `<div class="circular red"> </div>`;
    }
    // select_Player1.innerHTML= `<div class="circular red"> </div>`;
    // select_Player2.innerHTML= `<div class="circular blue"> </div>`;
    // select_Player3.innerHTML= `<div class="circular green"> </div>`;
    // select_Player4.innerHTML= `<div class="circular yellow"> </div>`;
    // document.querySelector('.colorBox').innerHTML = '<div class="circular red"></div><div class="circular blue"></div>  <div class="circular green"}></div> <div class="circular yellow"></div>'
  }

  const startGame =() => {
    setStart(true);
  }

  const onPlayerSelect =(term) => {
    setNumberPlayers(term);
  }

  const PlayersScoreCalc = (score) => {
    while(activePlayer <= parseInt(numberPlayers)){
      let player = 'player' + activePlayer;
      setPlayerScore({...playerScore, player: score});
    }
    activePlayer=== 1 && setPlayerScore({...playerScore, player1: score});
    activePlayer=== 2 && setPlayerScore({...playerScore, player2: score});
    activePlayer=== 3 && setPlayerScore({...playerScore, player3: score});
    activePlayer=== 4 && setPlayerScore({...playerScore, player4: score});
    select_Player1.innerHTML=`<div> </div>`;
    select_Player2.innerHTML=`<div> </div>`;
    select_Player3.innerHTML=`<div> </div>`;
    select_Player4.innerHTML=`<div> </div>`;
    activePlayer=== 4 ? setActivePlayer(1) : setActivePlayer(activePlayer+1);
  }


  const resetGame = () => {
    setNumberPlayers(1);
    setStart(false);
    setPlayerScore({player1: 0,player2: 0});
    document.querySelector(`.cell${playerScore.player1}`).innerHTML=`<div> </div>`;
    document.querySelector('.sortOrder').innerHTML='<select className="sortOrder" onChange= {e => setNumberPlayers(e.target.value)}><option value="1">1</option> <option value="2">2</option><option value="3">3</option><option value="4">4</option></select>';
  }
  console.log(playerScore);

  return (

    <div className="grid-container">
      <header className="header">
        <div> <button type="button" className="button" onClick={resetGame}>Reset</button></div>
        <div>Snake and ladder Game</div>
        <div> <button type="button" className="button" onClick={startGame}>Start</button></div>
      </header>

      <main className="main">
        <Gamegrid 
          startFlag={start} 
          totalPlayerSelect={onPlayerSelect} 
          players={numberPlayers} 
          score={activePlayer==='1' ? playerScore.player1 : playerScore.player2}
          ScoreCalc ={PlayersScoreCalc}/>
      </main>

      <footer className="footer">
        <div> Please Select Color box</div>
      </footer>
    </div>

  );
}

export default App;
