import React, { useState } from 'react';
import './App.css';
import Gamegrid from './components/Game_grid';

function App() {

  const [start, setStart] = useState(false);
  const [numberPlayers, setNumberPlayers] = useState(1);
  const [playerScore, setPlayerScore] = useState(0);


  const startGame =() => {
    setStart(true);
  }

  const onPlayerSelect =(term) => {
    setNumberPlayers(term);
  }

  const PlayersScoreCalc = (score) => {
    setPlayerScore(score);
  }
  if(playerScore>14){
    setStart(false);
  }

  const resetGame = () => {
    setNumberPlayers(1);
    setStart(false);
    setPlayerScore(0);
    document.querySelector(`.cell${playerScore}`).innerHTML=`<div> </div>`;
    document.querySelector('.sortOrder').innerHTML='<select className="sortOrder" onChange= {e => setNumberPlayers(e.target.value)}><option value="1">1</option> <option value="2">2</option><option value="3">3</option><option value="4">4</option></select>';
  }
  return (

    <div className="grid-container">
      <header className="header">
        <div> <button type="button" className="button" onClick={resetGame}>Reset</button></div>
        <div>Snake and ladder Game</div>
        <div> <button type="button" className="button" onClick={startGame}>Start</button></div>
      </header>

      <main className="main">
        <Gamegrid 
          startFlag={start} 
          totalPlayerSelect={onPlayerSelect} 
          players={numberPlayers} 
          score={playerScore}
          ScoreCalc ={PlayersScoreCalc}/>
      </main>

      <footer className="footer">
        <div> Please Select Color box</div>
      </footer>
    </div>

  );
}

export default App;
