import React, { useEffect, useState } from 'react';

function Timmer() {
    const [seconds, setSeconds] = useState(10);
    useEffect(() => {
      setSeconds(setInterval(seconds, 1000));
    }, []);
  
    useEffect(() => {
      tick();
    });
  
    function tick() {
      if (seconds > 0) {
        setSeconds(seconds - 1)
      } else {
        clearInterval(seconds);
      }
    }
  
    return (
  
        <div>
            {seconds}
        </div>
    );
  }
  
  export default Timmer;
