import React, { useEffect, useState } from 'react';

function Timmer() {
    const [seconds, setSeconds] = useState(10);

    useEffect(() => {
        if (seconds > 0) {
            setTimeout(() => setSeconds(seconds - 1), 1000);
          } else {
            setSeconds('BOOOOM!');
          }
    });
  
    return (
  
        <div>
            {seconds}
        </div>
    );
  }
  
  export default Timmer;
