import React, { useState } from 'react';

function Dice(props) {
    const [dice, setDice] = useState(1);


    const rollDice =() => {
        let randomNumber = Math.floor(Math.random() * 3) + 1;
        let score = props.playerScore + randomNumber;
        setDice(randomNumber);       
        props.totalScoreCalc(score);
    }
    
    console.log(props);
    return (
    <div>
        {props.startFlag 
        ? 
            <img className="dice_image" src={`/images/dice-${dice}.png`} alt={`dice-${dice}`} onClick={rollDice}></img>
        : <div>Please Click on Start button to start th Game</div>
        }
        
        {(props.playerScore>14) && (<div>Player1 is winner</div>)}
    </div>
    
    );
}

export default Dice;
