import React, { useState } from 'react';

function Dice() {
    const [dice, setDice] = useState(1);

    const rollDice =() => {
        let randNumber = Math.floor(Math.random() * 3) + 1;
        setDice(randNumber);
    }
    
    return (
    <div>
        <button className="button" onClick={rollDice}></button>
        <img className="dice_image" src="/images/dice-1.png"></img>
    </div>
    
    );
}

export default Dice;
