import React, { useState } from 'react';

function Dice() {
    const [dice, setDice] = useState(1);

    const rollDice =() => {
        let randNumber = Math.floor(Math.random() * 3) + 1;
        setDice(randNumber);
    }
    
    return (
    <button className="button" onClick={rollDice}>{dice}</button>
    );
}

export default Dice;
