import React, { useState } from 'react';

function Dice(props) {
    const [dice, setDice] = useState(1);


    const rollDice =() => {
        let randomNumber = Math.floor(Math.random() * 3) + 1;
        let score
        if(props.activePlayer === 1){
            score = props.playerScore.player1 + randomNumber;
        }else if(props.activePlayer === 2){
            score = props.playerScore.player2 + randomNumber;
        }else if(props.activePlayer === 3){
            score = props.playerScore.player3 + randomNumber;
        }else if(props.activePlayer === 4){
            score = props.playerScore.player4 + randomNumber;
        }
        setDice(randomNumber);       
        props.totalScoreCalc(score);
    }
    
    console.log(props);
    return (
    <div>
        {props.startFlag 
        ? 
            <img className="dice_image" src={`/images/dice-${dice}.png`} alt={`dice-${dice}`} onClick={rollDice}></img>
        : <div>Please Click on Start button to start th Game</div>
        }
        
        {(props.playerScore>14) && (<div>Player1 is winner</div>)}
    </div>
    
    );
}

export default Dice;
