import React, { useEffect, useState } from 'react';

function Timmer(props) {
    const [seconds, setSeconds] = useState(10);

        // useEffect(() => {
        //     setSeconds(10);
         
        //  },[props.activePlayer]);

    useEffect(() => {
        if (seconds >= 0) {
            setTimeout(() => setSeconds(seconds - 1), 1000);
          } else {
            setSeconds(10);
          }
    },[props.activePlayer]);


  
    return (
  
        <div className="timmer">
            {seconds}
        </div>
    );
  }
  
  export default Timmer;
