import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  const numbers = ['win',14,13,12,11,10,9,8,7,6,5,4,'start',1,2,3];

  return (

    <div className="grid-container">
      <header className="header">
        <div> <button type="button" className="button">Reset</button></div>
        <div>Snake and ladder Game</div>
        <div> <button type="button" className="button">Start</button></div>
      </header>

      <main className="main">
        <div className="input_item">

            Select Number of Players
            <select className="sortOrder">
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
            </select>

        </div>
        <div className="ui_grid">
          {numbers.map(number => <div class="cell" key={number}>{number}</div>)}
        </div>
        <div className="main_bttom">
            Bottom
        </div>
      </main>

      <footer className="footer">
        <div> Please Select Color box</div>
      </footer>
    </div>

  );
}

export default App;
